<?phpfunction mailText(array $send_data){    $userAge='';    $userHelp ='';    $userCar='';    $userLang='';    foreach ($send_data['userAge'] as $value){        $userAge = $value;    }    foreach ($send_data['canHelp'] as $value){        $userHelp .= ';'. $value;    }    foreach ($send_data['haveCar'] as $value){        $userCar = $value;    }    foreach ($send_data['lang'] as $value){        $userLang .= ';'. $value;    }    return '        Ім`я та прізвище :'.  $send_data['fio']."\r\n".        'Email* :'.  $send_data['email']."\r\n".        'Номер телефона*  :'.  $send_data['tel']."\r\n".        'Вкажіть область**  :'.  $send_data['city']."\r\n".        'Тобі вже виповнилося 18 років?* *   :'.  $userAge."\r\n" .        'Як ти можеш допомогти?  :'.  $userHelp."\r\n" .        'У тебе є машина?*  :'.  $userCar."\r\n" .        'Якою мовою розмовляєш?  :'.  $userLang."\r\n" .        'В який час ти вільний/вільна?  :'.  $send_data['freeTime']."\r\n" .        'Чому ти хочеш зайнятися волонтерством?  :'.  $send_data['why']."\r\n" .        'Є ще щось, що хочеш нам написати?  :'.  $send_data['another']."\r\n" . '';}; function sendMail(array $send_data){    $config = require_once ('config.php');    $to      = 'lovin.a@vuso.ua';    $subject = 'the subject';    $message = mailText($send_data);    $headers = 'From: air@example.com' . "\r\n" .        'Reply-To: air@example.com' . "\r\n" .        'X-Mailer: PHP/' . phpversion();    mail($to, $subject, $message, $headers);     return [         'status' => true,         'data' => $send_data     ];}